#! /bin/bash

set -e
set -o pipefail

cd $(dirname "$BASH_SOURCE")

key=$(./clinacl.py secretgen)
nonce=$(head -c 24 /dev/zero | xxd -p)
echo with nonce | ./clinacl.py encrypt $key --nonce $nonce | ./clinacl.py decrypt $key
echo without nonce | ./clinacl.py encrypt $key | ./clinacl.py decrypt $key

signingkey=$(./clinacl.py signinggen)
verifykey=$(./clinacl.py verifygen $signingkey)
echo verified | ./clinacl.py sign $signingkey | ./clinacl.py verify $verifykey

link=''
echo $link | ./clinacl.py keybase
